package com.atos.library;import com.atos.library.engines.DistributionEngine;import com.atos.library.engines.SearchEngine;import com.atos.library.engines.SearchInfoEngine;import com.atos.library.engines.impl.BooksDistributionEngine;import com.atos.library.engines.impl.BooksSearchEngine;import com.atos.library.engines.impl.BooksSearchInfoEngine;import com.atos.library.model.Book;import com.atos.library.model.Library;public class App {    public static void main(String[] args) {        Library library = initializeLibrary();        runAppDemo(library);    }    private static Library initializeLibrary() {        DistributionEngine distributionEngine = new BooksDistributionEngine();        SearchInfoEngine infoEngine = new BooksSearchInfoEngine();        SearchEngine searchEngine = new BooksSearchEngine();        return new Library(distributionEngine, infoEngine, searchEngine);    }    private static void runAppDemo(Library library) {        System.out.println("---------Creating books---------\n");        Book book1 = new Book("Kathy Sierra", "Head First Java", 2003);        Book book2 = new Book("Randall Munroe", "What If", 2014);        Book book3 = new Book("Sue Townsend", "The Secret Diary of Adrian Mole, Aged 13 3/4", 1982);        Book book4 = new Book("Gareth M. James", "An Introduction to Statistical Learning", 2013);        Book book5 = new Book("Sue Townsend", "Adrian Mole: The Capuccino Years", 2003);        Book book6 = new Book("Sue Townsend", "The Growing Pains of Adrian Mole", 1984);        System.out.println("---------Adding books to the library---------\n");        library.addBook(book1);        library.addBook(book2);        library.addBook(book3);        library.addBook(book4);        library.addBook(book5);        library.addBook(book6);        System.out.println("---------Checking library status and books by ID---------\n");        library.checkLibraryStatus();        library.checkBookById(6);        library.checkBookById(0);        System.out.println("---------Attempting to lend books---------\n");        library.lendBook(6, "Ewa");        library.lendBook(0, "John");        System.out.println("---------Attempting to remove books---------\n");        library.removeBook(6);        library.removeBook(0);        library.removeBook(1);        System.out.println("---------Checking library status and books by ID---------\n");        library.checkLibraryStatus();        library.checkBookById(6);        library.checkBookById(0);        library.checkBookById(1);        System.out.println("---------Checking books using search engine---------\n");        library.checkBooksByTitle("Invalid Title");        library.checkBooksByTitle("mole");        library.checkBooksByAuthor("townsend");        library.checkBooksByAuthorAndTitle("sue", "capuccino years");        library.checkBooksByYear(2003);        library.checkBooksByYear(2040);    }}