package com.atos.library.engines;import com.atos.library.engines.exceptions.InvalidIdException;import com.atos.library.model.Book;import java.util.List;import java.util.Optional;public interface SearchEngine {    Book findBookById(List<Book> books, int id) throws InvalidIdException;    Optional<List<Book>> findBooksByAuthor(List<Book> books, String author);    Optional<List<Book>> findBooksByAuthorAndTitle(List<Book> books, String author, String title);    Optional<List<Book>> findBooksByAuthorAndYear(List<Book> books, String author, int year);    Optional<List<Book>> findBooksByTitle(List<Book> books, String title);    Optional<List<Book>> findBooksByYear(List<Book> books, int year);    Optional<List<Book>> findBooksByYearAndTitle(List<Book> books, int year, String title);}