package com.atos.library.engines.impl;import com.atos.library.engines.DistributionEngine;import com.atos.library.model.Book;import java.util.List;public class BooksDistributionEngine implements DistributionEngine {    private static final String MESSAGE_BOOK_LENT = "Sorry, the book with id: %d is already lent to: %s\n";    private static final String MESSAGE_BOOK_UNAVAILABLE = "Sorry, the book with id: %d is currently unavailable\n";    private static final String MESSAGE_INVALID_ID = "Sorry, there is no book with such id\n";    private static final String MESSAGE_LENDING = "The book with id: %d has been successfully lent to: %s\n";    private static final String MESSAGE_REMOVAL = "The book with id: %d has been successfully removed from the" +            " library\n";    @Override    public void addBook(List<Book> books, Book book) {        books.add(book);    }    @Override    public void lendBook(List<Book> books, int id, String reader) {        if (books.size() > id) {            Book book = books.get(id);            if (book.isAvailable()) {                book.setAvailable(false);                book.setReader(reader);                System.out.println(String.format(MESSAGE_LENDING, id, reader));            } else {                System.out.println(String.format(MESSAGE_BOOK_LENT, id, book.getReader()));            }        } else {            System.out.println(MESSAGE_INVALID_ID);        }    }    @Override    public void removeBook(List<Book> books, int id) {        if (books.size() > id) {            if (books.get(id).isAvailable()) {                books.remove(id);                System.out.println(String.format(MESSAGE_REMOVAL, id));            } else {                System.out.println(String.format(MESSAGE_BOOK_UNAVAILABLE, id));            }        } else {            System.out.println(MESSAGE_INVALID_ID);        }    }}