package com.atos.library.engines.impl;import com.atos.library.engines.SearchInfoEngine;import com.atos.library.model.Book;import java.util.List;import java.util.Optional;public class BooksSearchInfoEngine implements SearchInfoEngine {    private static final String MESSAGE_NO_SEARCH_RESULTS = "Sorry, there are no matching results\n";    private static final String MESSAGE_BOOKS_AVAILABILITY = "The are %d/%d available books in our library.\nFor " +            "the complete list of books please refer to the status below:\n------";    private static final String MESSAGE_INVALID_ID = "Sorry, there is no book with such id\n";    private static final String NEW_LINE = "\n";    @Override    public void printBookSearchResult(Optional<Book> book) {        if (book.isPresent()) {            System.out.println(book.get().toString() + NEW_LINE);        } else {            System.out.println(MESSAGE_INVALID_ID);        }    }    @Override    public void printBooksSearchResults(Optional<List<Book>> books) {        if (books.isPresent()) {            books.get().forEach(System.out::println);            System.out.println(NEW_LINE);        } else {            System.out.println(MESSAGE_NO_SEARCH_RESULTS);        }    }    @Override    public void printLibraryStatus(List<Book> books) {        int availableBooksNumber = (int) books.stream()                .filter(Book::isAvailable).count();        System.out.println(String.format(MESSAGE_BOOKS_AVAILABILITY, availableBooksNumber, books.size()));        books.forEach(System.out::println);        System.out.println(NEW_LINE);    }}