package com.atos.library.model;import org.apache.commons.lang3.StringUtils;public class Book {    private String author;    private String title;    private int year;    private boolean isAvailable;    private String reader;    public Book(String author, String title, int year) {        this.author = author;        this.title = title;        this.year = year;        this.isAvailable = true;        this.reader = StringUtils.EMPTY;    }    public String getAuthor() {        return author;    }    public String getTitle() {        return title;    }    public int getYear() {        return year;    }    public boolean isAvailable() {        return isAvailable;    }    public void setAvailable(boolean available) {        isAvailable = available;    }    public void setReader(String reader) {        this.reader = reader;    }    public String getReader() {        return reader;    }    @Override    public String toString() {        return "Book{" +                "author='" + author + '\'' +                ", title='" + title + '\'' +                ", year=" + year +                ", isAvailable=" + isAvailable +                ", reader='" + reader + '\'' +                '}';    }}